```cpp
#include <queue>
#include <stack>
#include <cstdio>
using namespace std;

int beg[155];
int ed[22505];
int nxt[22505];
int li[22505];
int vi[22505];
int top;

void addedge(int a,int b,int l,int v)
{
	++top;
	ed[top] = b;
	li[top] = l;
	vi[top] = v;
	nxt[top] = beg[a];
	beg[a] = top;
}

int dn;
int undist[155][155];
int unpre[155][155];
int vis[155];

void undisdij(int x)
{
	for(int i=1; i<=dn; ++i)
	{
		undist[x][i] = 0x7f7f7f7f;
		vis[i] = 0;
	}

	undist[x][x] = 0;
	priority_queue<pair<int,int> > pq;
	pq.push(make_pair(0,x));

	while(!pq.empty())
	{
		int th = pq.top().second;
		pq.pop();
		if(vis[th]) continue;

		vis[th] = 1;

		for(int p=beg[th]; p; p=nxt[p])
		{
			if(!vi[p] && undist[x][ed[p]]>undist[x][th]+li[p])
			{
				unpre[x][ed[p]] = th;
				undist[x][ed[p]] = undist[x][th]+li[p];
				pq.push(make_pair(-undist[x][ed[p]],ed[p]));
			}
		}
	}
}

double dist[155];
int type[155];
int pre[155];
int ppre[155];

void dijkstra(int s)
{
	priority_queue<pair<double,int> > pq;	
	for(int i=1; i<=dn; ++i)
	{
		pre[i] = s;
		ppre[i] = s;
		type[i] = 0;
		dist[i] = 1.*undist[s][i]/70;
		pq.push(make_pair(-dist[i],i));
		vis[i] = 0;
	}

	while(!pq.empty())
	{
		int th = pq.top().second;
		pq.pop();
		if(vis[th]) continue;

		vis[th] = 1;

		for(int p=beg[th]; p; p=nxt[p])
		{
			if(vi[p])
			{
				if(dist[ed[p]] > dist[th]+(1.*li[p]/vi[p]))
				{
					dist[ed[p]] = dist[th] + (1.*li[p]/vi[p]);
					type[ed[p]] = 1;
					pre[ed[p]] = th;
					pq.push(make_pair(-dist[ed[p]],ed[p]));
				}
				for(int i=1; i<=dn; ++i)
