{
    dp[x]=1;
	for(int i=0;i<s[x].size();i++)
	{
		if(s[x][i].t!=fa)
		{
			p[s[x][i].t]=s[x][i].p;
			dfs(s[x][i].t,x);
			dp[x]=max(dp[x],dp[s[x][i].t]+s[x][i].p);
		}
	}
	if(dp[x]+p[x]>k)
	{
		ans++;
		dp[x]=1;
	}
}
int main()
{
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
	{
		int m;
