## f[i][0]表示不放，f[i][1]表示要放
------------
```cpp
#include<bits/stdc++.h>
#define N 20005
using namespace std; 
int n,head[N],cnt,frist,f[N][2],v[N];
bool flag=true;
struct node{
	int v,next,w;
}e[N<<1];
inline void add_edge(int u,int v,int w)
{
	e[++cnt].v=v;
	e[cnt].next=head[u];
	e[cnt].w=w;
	head[u]=cnt;
}
inline void dfs(int x,int str)
{
	v[x]=1;//表示已经访问（双向边）
	int ant=0;
	for(int i=head[x];i;i=e[i].next)
	{
		if(v[e[i].v]) continue;
		if(e[i].w>=frist)
		{
			printf("No solution.\n");
			flag=false;
			exit(0);
		}
		ant++;
		if(str>e[i].w)//若大于衰减强度则可以有不放这种情况
		{
			dfs(e[i].v,str-e[i].w);//一搜到底在回溯
			f[x][0]=min(f[e[i].v][0],f[e[i].v][1]);
		}
		dfs(e[i].v,frist);//一搜到底在回溯
		f[x][1]=min(f[e[i].v][0]+1,f[e[i].v][1]+1);
	}
	if(!ant)//一个子节点也没有
	{
		f[x][0]=0;
		f[x][1]=1;
		return;		
	}
}
int main()
{
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
	{
		int k;
		f[i][0]=f[i][1]=1e9;//初始化
		scanf("%d",&k);
		for(int j=1;j<=k;j++)
		{
			int v,ww;
			scanf("%d %d",&v,&ww);
			add_edge(i,v,ww);
		}
	}
	scanf("%d",&frist);
	dfs(1,frist);
	int ans=min(f[1][0],f[1][1]);
	if(flag) printf("%d\n",ans);
	return 0;
}
```

贪心+树形结构；

我们设dis[x]表示x子树内到x最深距离。然后从叶子节点开始往上爬，找到一个点，dis[x]+他到他父亲的距离大于初始信号强度，那么这个点需要且必须放一个信号放大器，然后把这个点的dis[x]赋为0. 之后一直按这种只有到不得不放信号放大器的时候才放信号放大器，这样得到的结果就是最优。
```
#include <cstdio>
#include <iostream>
#include <cstring>
using namespace std;

const int N=2e4+4;
int First[2*N],Next[2*N],go[2*N],w[2*N],tot;
int dis[N],father[N];
int n,m,k,x,y,z,s,maxn,ans;

inline void add(int u,int v,int c)
{
	Next[++tot]=First[u]; First[u]=tot; go[tot]=v; w[tot]=c;
}

inline void dfs(int u,int fa)
{
	for(int i=First[u];i;i=Next[i])
	{
		int v=go[i];
		if(v!=fa) 
		{
			father[v]=w[i];
			dfs(v,u);
			dis[u]=max(dis[u],dis[v]+w[i]);
		}
	}
	if(dis[u]+father[u]>s) 
	{
		ans++;
		dis[u]=0;
	}
}

int main( )
{
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
	{
		scanf("%d",&k);
		for(int j=1;j<=k;j++)
		{
			scanf("%d%d",&x,&y);
			add(i,x,y);
			maxn=max(maxn,y);
		}
	}
	scanf("%d",&s);
	dfs(1,1);
	if(maxn>=s) printf("No solution.\n");
	else printf("%d\n",ans);
	return 0;
}
```

#第一种：搜索加优化

  f[x][sum]=min(∑f[v][sum-l],∑f[v][res-l]+1)

本人蒟蒻不会判断回路，用的去双向边

```cpp
#include <iostream>
#include <cmath>
#include <cstring>
#include <cstdio>
#include <cstdlib>
using namespace  std;

const int M=100010,inf=20000000;
struct f1
{
  int nx,l,v;
}e[M];
int head[M],i,j,k,n,res,t,ans,u,v,Max;
int b[M];

void zs(int x)//去双向边
{
  b[x]=1;
  if(head[x]==0) return;
  int i=head[x];
  while(i!=0&&b[e[i].v]) 
    i=e[i].nx;
  head[x]=i;
  if(i==0) return; 
  int j=i;
  while(j>0)
  {
    if(b[e[j].v])
    {
      e[i].nx=e[j].nx;
      j=e[j].nx;
    }
    else 
    {
      zs(e[j].v);
      i=j;
      j=e[j].nx;
    }
  }
}

void init()
{
  scanf("%d",&n);
  for(i=1;i<=n;i++)
  {
    scanf("%d",&k);
    for(j=1;j<=k;j++)
    {
      scanf("%d %d",&u,&v);
      if(v>Max) Max=v; 
      t++; e[t].v=u; e[t].l=v; e[t].nx=head[i]; head[i]=t;
    }
  }
  zs(1);
  memset(b,0,sizeof(b));
  scanf("%d",&res);
}

int dfs(int x,int sum)
{
  int j=head[x],Min=inf,tmp=0;
  bool fl=false;
  while(j>0)
  {
    if(sum-e[j].l<=0) { fl=true; break; }
    j=e[j].nx;
  }
  if(!fl)
  {
    j=head[x];
    while(j>0)
    {
      int v=e[j].v;
      tmp+=dfs(v,sum-e[j].l);
      j=e[j].nx;
    }
    if(tmp<Min) Min=tmp;
  }
  if(Min>1)//优化点
  {
    tmp=1;
    j=head[x];
    while(j>0)
    {
      int v=e[j].v;
      tmp+=dfs(v,res-e[j].l);
      if(tmp>Min) break;//优化点
      j=e[j].nx;
    }
    if(tmp<Min) Min=tmp;
  }
  return Min;
}

int main()
{
  freopen("booster.in","r",stdin);
  freopen("booster.out","w",stdout);
  init();
  if(Max>=res)  printf("No solution.\n");
  else  cout<<dfs(1,res)<<endl;
  fclose(stdin);fclose(stdout);
  return 0;
} 
