    if (! fil[i][j] && (i != x || j != y)) {flag = 0; break;}
      }
    if (flag && ans != 34) return 0;
  }
  return 1;
}
void dfs (int x, int y) {
  if (y > 4) y = 1, x ++;
  if (x > 4) {prin(); return;}
  if (fil[x][y] == 1)
    dfs (x, y + 1);
  else
    for (int i = 1; i <= 16; i++)
      if (! use[i] && Judge (x, y, i))
    use[i] = 1, fil[x][y] = i, dfs (x, y + 1), fil[x][y] = 0, use[i] = 0;
}
int main()
{
  scanf ("%d %d", &x, &y);
  fil[x][y] = 1, use[1] = 1;
  dfs (1, 1);
  return 0;
}
```

~~这道题就是一个裸的暴搜我还是写了这么久QAQ~~

因为题目信息给的太多，4*4以及很多和要为34，剪枝很好剪，我就直接开挂对每个位置特判一下。比如说在(1,4)的时候检查第一行是否为34，在(1,4)时检查第一列的和和对角线的和。然后在调试的时候我发现，对于样例中(1,1)为1的情况 第一列会是这样：

```
第一次:1 2 2 X 返回
第二次:1 2 3 X 返回

```
我觉得我是该做点什么来阻止这愚蠢的行为了。于是我在每个要求的区域和为34的第三个点特判，先把前两个的和算出来，（当前要填的为i）

```
i=max(i,34-mx[1][2]-mx[1][1]-16);
if(i>16) return ;
```

意思应该很好懂，这是(1,3)处的特判。然后我又发现这一列的和明明前三个就已经大于34了还在搜, = =,真是看不下去了。于是对于每个搜到的点计算列之和还有行之和，大于34直接return。看来效果好像不错的样子，最慢的一个点0.2s。

# 代码

（放大字体就为了让你们注意到QAQ）

```
#include<queue>
#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;

int I,J,sum,used[20];
int mx[5][5],cnt=0;

void dfs(int x,int y)
{
    if(x==5&&y==1)
    {
        cnt++;
        for(int i=1;i<=4;printf("\n"),i++)
          for(int j=1;j<=4;j++)
            printf("%d ",mx[i][j]);
        printf("\n");
        return ;
    }
    int l=2,r=16;
    if(x==I&&y==J) l=r=1;
    for(int i=l;i<=r;i++)
    {
//      printf("%d %d %d\n",cnt,x,y);
        if(y==4) {//每行
            sum=0;
            for(int j=1;j<4;j++)
              sum+=mx[x][j];
            sum+=i;
            if(sum>34||sum+16<34) return;
            else if(sum!=34) i=34-(sum-i);
        }
        else{
            sum=0;
            for(int j=1;j<y;j++)
                sum+=mx[x][j];sum+=i;
            if(sum>34) return ;
        }
        if(x==4){//每列 
            sum=0;
            for(int j=1;j<4;j++)
              sum+=mx[j][y];
            sum+=i;
            if(sum>34||sum+16<34) return;
            else if(sum!=34) i=34-(sum-i);
        }
        else{
            sum=0;
            for(int j=1;j<x;j++)
                sum+=mx[j][y];sum+=i;
            if(sum>34) return ;
        }
        if(x%2==0&&y%2==0){//角落格子 
            sum=mx[x-1][y-1]+mx[x-1][y]+mx[x][y-1]+i;
            if(sum>34||sum+16<34) return ;
            else if(sum!=34) i=34-(sum-i);
        }
        if(x==1&&y==3){
            i=max(i,34-mx[1][2]-mx[1][1]-16);
            if(i>16) return ;
        }
        else if(x==2&&y==1){
            i=max(i,34-mx[1][1]-mx[1][2]-16);
            if(i>16) return ;
        }
        else if(x==2&&y==3){
            i=max(i,34-16-mx[1][3]-mx[1][4]);
            i=max(i,34-16-mx[2][1]-mx[2][2]);
            if(i>16) return ;
        }
        else if(x==3&&y==2){
            i=max(i,34-16-mx[1][4]-mx[2][3]);
            if(i>16) return ;
        }
        else if(x==3&&y==3){//中间一坨 
            sum=mx[2][2]+mx[2][3]+mx[3][2]+i;
            if(sum>34||sum+16<34) return ;
            else if(sum!=34) i=34-(sum-i);
            i=max(i,34-16-mx[1][1]-mx[2][2]);
            i=max(i,34-16-mx[3][1]-mx[3][2]);
            if(i>16) return ;
        }
        else if(x==4&&y==1){
            i=max(i,34-16-mx[3][1]-mx[3][2]);
            if(i>16) return ;
        }
        else if(x==4&&y==3){
            i=max(i,34-16-mx[3][3]-mx[3][4]);
            if(i>16) return ;
        }
        else if(x==4&&y==1){//左下对角线 
            sum=mx[1][4]+mx[2][3]+mx[3][2]+i;
            if(sum>34||sum+16<34) return ;
            else if(sum!=34) i=34-(sum-i);
        }
        else if(x==4&&y==4){
            sum=mx[1][1]+mx[2][2]+mx[3][3]+i;
            if(sum>34||sum+16<34) return ;
            else if(sum!=34) i=34-(sum-i);
            sum=mx[1][1]+mx[1][4]+mx[4][1]+i;
            if(sum>34||sum+16<34) return ;
            else if(sum!=34) i=34-(sum-i);
        }
        if(used[i]) continue;
        mx[x][y]=i;
        used[i]=1;
        int nx=x,ny=y+1;
        if(ny==5) nx=x+1,ny=1;
        dfs(nx,ny);
        used[i]=0;
        mx[x][y]=0;
    }
}

int main()
{
    scanf("%d%d",&I,&J);
    dfs(I,J);
    return 0;
}
```
