		for(rint j = 1;j <= 4;j++)
		{
			write(a[i][j]);
			putchar(' ');
		}
		puts("");
	}
	puts("");
	return;
}

void dfs(int x,int y)
{
	if(x == 4 && y == 4)
	{
		if(final_check() == 0) return;

		print();
		return; 
	}
	if(during_work_check(x,y) == 0)
		return;

	for(rint i = 2;i <= 16;i++)
	{
		if(vis[i]) continue;//取过就不取
		rint tarx,tary;
		if(y == 4)//取到一行末尾 
		{
			tarx = x + 1;
			tary = 1;
		}
		else//行中
		{
			tarx = x;
			tary = y + 1;
		}
		if(a[tarx][tary])
		{
        //之前有数就不用确定数了，直接一次跳进下一个
			dfs(tarx,tary);
			break;
		}
		else//选个数
		{
			a[tarx][tary] = i; 
			vis[i] = 1;

			dfs(tarx,tary); 

        //回溯的时候一定把数字还原，把标记也还原
			vis[i] = 0;
			a[tarx][tary] = 0;
		}

	} 
}

int main()
{
	readint(xx),readint(yy);
	memset(a,0,sizeof(a));
	memset(vis,0,sizeof(vis));
	vis[1] = 1;
	a[xx][yy] = 1;
	dfs(1,0);
	return 0;
}
