```cpp
#include <iostream>
#include <cstdio>
#include <cstring>
#include <algorithm>

using namespace std;

struct Node{
	char l,r;
	int w;
	inline bool operator<(const Node&x)const{//排序
		if(l!=x.l){
			return l<x.l;
		}else{
			if(r!=x.r){
				return r<x.r;
			}else{//保证首尾字母相同的情况下从大到小
				return w>x.w;
			}
		}
	}
}a[20];

int n,ans,len;
char s[1000];
bool vis[20];//vis表示是否用过这个单词

inline int dfs(int x){
	int sum=0;
	for(int i=1;i<=n;i++){//枚举下一个单词
		if(a[x].r==a[i].l&&!vis[i]){//判断是否可行
			vis[i]=true;
			sum=max(sum,a[i].w+dfs(i));
			vis[i]=false;
			while(a[i+1].l==a[i].l&&a[i+1].r==a[i].r)i++;
		}//唯一的剪枝
	}
	return sum;
}

int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%s",s+1);
		len=strlen(s+1);
		a[i].l=s[1];a[i].r=s[len];a[i].w=len;//单词中间的字母是没有用的
	}
	sort(a+1,a+n+1);
	for(int i=1;i<=n;i++){//枚举第一个单词
		vis[i]=true;
		ans=max(ans,a[i].w+dfs(i));
		vis[i]=false;
	}
	printf("%d\n",ans);
	return 0;
}
```

