#include <iostream>
#include <cstdio>
#include <algorithm>
#include <string>

using namespace std;

const int MAXN = 50;

int get_num(char cur) {//将字符映射成数字
	if(cur == 'A') return 1;
	if(cur == 'E') return 2;
	if(cur == 'I') return 3;
	if(cur == 'O') return 4;
	if(cur == 'U') return 5;
}

string s;
int n,ans;
int head[MAXN],t[MAXN],w[MAXN],nxt[MAXN],cnt;
bool vis[MAXN];

void addedge(int u,int v,int wei) {
	t[++cnt] = v;
	w[cnt] = wei;
	nxt[cnt] = head[u];
	head[u] = cnt;
}

void DFS(int u,int sum) {
	ans = max(ans,sum);
	for(int i = head[u]; i; i = nxt[i]) {
		if(vis[i]) continue;
		vis[i] = true;
		DFS(t[i],sum + w[i]);
		vis[i] = false;
	}
	return;
}

int main() {
	scanf("%d",&n);
	for(int i = 1; i <= 5; i++) {//给每个点都预先加入一条自环
		addedge(i,i,0);
	}
   //点i的所有自环都将被合并到预先加入的自环上。
	for(int i = 1; i <= n; i++) {
		cin >> s;
		int len = s.length();
		if(s[0] == s[len - 1]) {
			w[get_num(s[0])] += len;//将首尾相等的字符串合并(合并自环)
		}
		else addedge(get_num(s[0]),get_num(s[len - 1]),len);
	}
	for(int i = 1; i <= 5; i++) {
		DFS(i,0);
	}
	cout << ans << endl;
	return 0;
}
