   f[i,1]:=s[i];//初始化
  end;
 for i:=2 to n do//DP
  for j:=2 to m do
   begin
    f[i,j]:=maxlongint;
    for k:=j-1 to i do
     f[i,j]:=min(f[i,j],max(f[k,j-1],s[i]-s[k]));//状态转移方程
   end;
 j:=n+1;l:=n;
 for i:=1 to m do//贪心，因为要给后面的人尽可能多的书，所以j从后面搞
