```
#include<iostream>
#include<cmath>
#include<cstring>

using  namespace std;

int n,m,a[100005];
//n本书,m个人
//a[]为每本书的时间

bool check(int x){
//这个函数是用来检查是否可以在x的时间内
//由m个人抄完整本书的
	int s=0,w=0;
    //s是最少所需人数,w是目前的人抄写时间
	for(int i=1;i<=n;i++){
		if(x<a[i]) return 0;
        //若这本书耗费的时间比x还长,直接return false
		if(w+a[i]>x){
			s++;
			w=0;
            //超出x,计下一个人
		}
		w+=a[i];
		if(s>m) return 0;
        //人数超了,false
	}
	if(w!=0) s++;
    //最后一个人
	if(s>m) return 0;
    //人数超了,false
	return 1;
}

int main()
{ 
	int l=0,r;
    //将时间的最小的可能计为l(下限)
	int f[600][5];
	int x,y,cnt=0,w=0;
	cin>>n>>m;
	for(int i=1;i<=n;i++){
		cin>>a[i];
		r+=a[i];
        //将时间的最大的可能计为r(上限)
	} 
	while(l<r){//二分
		int mid=(l+r)/2;
		if(check(mid)) r=mid;
        //若在目前的“最短时间”下m个人可以完成,
        //将上限变小,缩小范围
		else l=mid+1;
        //若在目前的“最短时间”下m个人不可以完成,
        //将下限变大,缩小范围
	}
    //得出最短时间(l) P1182就输出这个值就可以了
    //开始输出我们的方案
    //x是每一个人的范围的右端点
    //w是目前的人抄写时间
	x=n;
	for(int i=n;i>=1;i--){
		if(w+a[i]>l){
			f[++cnt][2]=x;
			f[cnt][1]=i+1;
            //将每个人的范围存到一个数组里
			x=i;
            w=0;
            //超出x,计下一个人
		}
		w+=a[i];
	}
	if(w!=0){
        //最后一个人
		f[++cnt][1]=1;
		f[cnt][2]=x;
	}
	for(int i=m;i>=1;i--) //输出
        cout<<f[i][1]<<" "<<f[i][2]<<endl;

    return 0;
}
