```C++
#include<iostream>
#include<cstdio>
#include<cctype>
#include<cmath>
#include<cstring>
#include<algorithm>
using namespace std;

int n,C,ans; //C表示颜色总数 
struct node {  
/*
感觉按照题中所说的坐标来容易跟笛卡尔坐标系混淆，
所以我就直接把它看成表格，把坐标看成行列
这样也比较好理解嘛qwq
*/
    int x1,y1; //左上角坐标（行列） 
    int x2,y2; //右下角坐标（行列） 
    int color; //颜色
}a[20];
int f[20][20]; //f[i][j]表示矩形i是否在矩形j上方 

int read() { //快读
    int x=0; char ch=getchar();
    while (!isdigit(ch)) ch=getchar();
    while (isdigit(ch)) {
        x=(x<<3)+(x<<1)+ch-'0';
        ch=getchar();
    }
    return x;
}

int cmp(node x,node y) {
    return (x.x1<y.x1||(x.x1==y.x1&&x.y1<y.y1));
}

void dfs(int s,int x) {
//s表示拿刷子的次数，x表示当前状态
    if (s>=ans) return ;
    if (x==(1<<n)-1) {
        ans=s;
        return ;
    }
    for (int i=1; i<=C; ++i) { //选择颜色 
        int newx=x; //记录状态 
        for (int j=1; j<=n; ++j) { //选择矩形
             if (newx&(1<<j-1)) continue; //已涂
             if (a[j].color!=i) continue; //不是这种颜色 
             int flag=1;
             for (int k=1; k<=n; ++k) { //检查是否可以涂
             	if (f[k][j]&&((newx&(1<<k-1))==0)) {
             	//矩形k在矩形j上方且未被涂色 
             		flag=0;
                    break;
                }
             }
             if (!flag) continue;
             newx=newx|(1<<j-1); //涂色
        }
        if (newx!=x) dfs(s+1,newx);
    }
}

int main() {
    n=read();
    for (int i=1; i<=n; ++i) {
        a[i].x1=read(); a[i].y1=read();
        a[i].x2=read(); a[i].y2=read();
        a[i].color=read();
        C=max(C,a[i].color);
    }
    sort(a+1,a+n+1,cmp); //强迫症OIer必须排个序才舒服
    for (int i=1; i<=n; ++i) {
        for (int j=1; j<=n; ++j) {
            f[i][j]=(a[i].x2==a[j].x1&&a[i].y2>a[j].y1&&a[i].y1<a[j].y2);
            //判断矩形i是否在矩形j上面 
        }
    }
    ans=n;
    dfs(0,0); //拿刷子0次，此时状态为0 
    printf("%d\n",ans);
    return 0;
}
```

