		long double p=(a[0]+a[1]+a[2])/2.0;  //海伦公式求三角形面积 
		ans=max(ans,(ll)(trunc(sqrt(p*(p-a[0])*(p-a[1])*(p-a[2]))*100LL)));
		//实测 trunk 加上后 运行速度变快 
	}
	wr(ans);
	return 0;
} 
