	for(int i = vec_tot;i >= 1;i--){
		for(auto x : vec[i][opt[i][now]])ans[0].push_back(x);
		for(auto x : vec[i][opt[i][now] ^ 1])ans[1].push_back(x);
		now -= vec[i][opt[i][now]].size();
