    for (register int i = 1; i <= tot; ++i){
        for (register int k = 0; k <= 1; ++k)
            for (register int j = cnt[i][k]; j <= n / 2; ++j){
                if (dp[i][j] || !dp[i - 1][j - cnt[i][k]])   continue;
                dp[i][j] = 1;
                sf[i][j] = k;
            }
    }
    int num;
    for (register int i = n / 2; i >= 1; --i){
        if (!dp[tot][i])    continue;
        num = i;
        get_list(tot, i);
        break;
    }
    printf("%d ", num);
    for (register int i = 1; i <= n; ++i)
        if (ans[i]) printf("%d ", i);

    printf("\n%d ", n - num);
    for (register int i = 1; i <= n; ++i)
        if (!ans[i])    printf("%d ", i);
    return 0;
}

