int f3[20];
int m;
void calc3(){//先计算出来前三个的值 
	double tmp;
	tmp=(f3[1]+f3[2]-f3[3])/2;
	mem(ans,0);
	if (tmp<0) return;
	if (floor(tmp)<tmp) return;
	ans[1]=(int)tmp;
	tmp=(f3[1]+f3[3]-f3[2])/2;
	if (tmp<0) return;
	if (floor(tmp)<tmp) return;
	ans[2]=(int)tmp;
	tmp=(f3[2]+f3[3]-f3[1])/2;
	if (tmp<0) return;
	if (floor(tmp)<tmp) return;
	ans[3]=(int)tmp;
}
