	mem(tmp,0);
	int cnt=0,cnt1=0,cnt2=0;
	for(int i=1;i<=m;i++){
		if (ans[1]<=sum[i])
		tmp[++cnt]=sum[i]-ans[1];
		if (ans[2]<=sum[i])
		tmp[++cnt]=sum[i]-ans[2];
		if (ans[3]<=sum[i])
		tmp[++cnt]=sum[i]-ans[3];
	}
	tmp[++cnt]=ans[1];//因为 calc3 里把存tans的去掉了,所以这里手动添加 
	tmp[++cnt]=ans[1];
	tmp[++cnt]=ans[2];
	tmp[++cnt]=ans[2];
	tmp[++cnt]=ans[3];
	tmp[++cnt]=ans[3];
	sort(tmp+1,tmp+cnt+1,comp1);	
	for(int i=1;i<=cnt;i++){//统计次数 
		if (tmp[i]!=tmp[i-1]){
			tans[++cnt1]=(node1){tmp[i],1};
