			if (sum[i]==sum[i-1]) ssum[mm].ti++;
			else ssum[++mm]=(node1){sum[i],1};
		}
		dfs(1,1);
		if (ret){
			putchar('\n');
			continue;
		}
		printf("Impossible\n");
	}
