    for (int i = 0; i < N; ++i)
    {
        cin >> k;
        v.push_back(k);
    }
    k = *(min_element(v.begin(), v.end()));
    auto first_itr = find(v.begin(), v.end(), k);
    auto last_itr = find(v.rbegin(), v.rend(), k);
    int first_dis = first_itr - v.begin();
    int last_dis = last_itr - v.rbegin();
    if (k != 0 && N % 2 == 1)
    {
        cout << "YES" << endl;
        return 0;
    }
    if (first_dis % 2 == 0 && last_dis % 2 == 0)
        cout << "NO" << endl;
    else
        cout << "YES" << endl;
