ll gcd(ll x,ll y){
	if(x==0){
		return y;
	}
	return gcd(y%x,x);
}

ll lcm(ll x,ll y){
	return x/gcd(x,y)*y;
}

ll duliu(ll x){
	ll res=0;
	while(x){
		x=x/10;
		res++;
	}
	return res;
}

int main(){
	cin>>n;
	for(ll i=1;i<=n;i++){
		ll g=gcd(i,n);
		a[i]=n/g;
		b[i]=i/g;
	}
	for(ll i=1;i<=n;i++){
		ans2=lcm(ans2,b[i]);
	}
	for(ll i=1;i<=n;i++){
		ans1=ans1+ans2/b[i]*a[i];
	}
	ll g=gcd(ans1,ans2);
	ans1=ans1/g;
	ans2=ans2/g;
	if(ans1%ans2==0){
		cout<<ans1/ans2<<endl;
		return 0;
	}
	ans3=ans1/ans2;
	ans1=ans1%ans2;
	ll duliu3=duliu(ans3);
	ll duliu1=duliu(ans2);
	for(ll i=1;i<=duliu3;i++){
		cout<<" ";
	}
	cout<<ans1<<endl;
	cout<<ans3;
	for(ll i=1;i<=duliu1;i++){
		cout<<"-";
	}
	cout<<endl;
	for(ll i=1;i<=duliu3;i++){
		cout<<" ";
	}
	cout<<ans2<<endl;
	return 0;
}
