#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cstring>
#define  ll long long
using namespace std;
void read(ll &x)
{
	x=0;int p=1;
	char c=getchar();
	while(c<'0'||c>'9')
	{
		if(c=='-') p=-1;
		c=getchar();
