}// 读入优化自动忽略。
ll gcd(ll x,ll y)
{
	if(y>x)
	{
		int tmp;
		tmp=x;
		x=y;
		y=tmp;
	}
	if(x%y==0)
	{
		return y;
	}
	else gcd(y,x%y);
}// 分数化简要用的最大公因数。
ll n,sum=1,p;// sum记录分母，p记录分子。
int main()
{
	read(n);
	p=n;
	for(int i=2;i<=n;i++)
	{
