		while(u>0)
		{
			num1++;
			u/=10;
		}// 求分母的位数，最终要使分子、分母与分数线对其输出。
		for(int i=1;i<=num;i++)
		 cout<<' ';
		cout<<o%sum<<endl<<o/sum;// 空格的位数等于整数部分的位数。
		for(int i=1;i<=num1;i++)
		 cout<<'-';// 减号的个数等于分母的位数。
		cout<<endl;
		for(int i=1;i<=num;i++)
		 cout<<' ';
		cout<<sum<<endl;
	}
	return 0;
}// AC！！

