#include<bits/stdc++.h> 
using namespace std;
struct city{
	int men,km,lb;
	string name;
}a[150];
bool cmp(city a,city b){
	if(a.lb!=b.lb) return a.lb<b.lb;
	return a.km<b.km;
}
int money=1000000,tot,lb;
string n;
int main(){
	while(cin>>a[tot].men>>a[tot].km>>a[tot].name) tot++;
	for(int i=0;i<tot;i++){
		a[i].lb=0;
		for(int j=0;j<tot;j++){
			a[i].lb+=abs(a[j].km-a[i].km)*a[j].men;
		}
	}
	sort(a,a+tot,cmp);
	cout<<a[0].name<<' '<<a[0].lb;
}
```
