#include<bits/stdc++.h>

#define M 150

using namespace std;

struct node{
	int num;
	int dis;
	string name;
}a[M + 5];

int many;
int f[M + 5][M + 5];
int ans;
int ff[M + 5][M + 5];

int main(){
	for(int i = 1;;i ++){
		int numm;
		int diss;
		string namee;
		cin>>numm>>diss>>namee;
		a[i].num = numm;
		a[i].dis = diss;
		a[i].name = namee;
		if(diss == 0 && namee == "Moscow"){
			many = i;
			break;
		}
	}
	for(int i = 1;i <= many - 1;i ++)
		for(int j = 1;j <= many - i;j ++)
			if(a[j].dis > a[j + 1].dis){
				swap(a[j].dis,a[j + 1].dis);
				swap(a[j].num,a[j + 1].num);
				swap(a[j].name,a[j + 1].name);
			}
	//Moscow是第1站
	 for(int i = 1;i <= many - 1;i ++)f[i][i + 1] = a[i + 1].dis - a[i].dis,f[i + 1][i] = f[i][i + 1];
	 for(int i = 1;i <= many - 1;i ++)ff[i][i + 1] = f[i][i + 1] * (a[i].num + a[i + 1].num);
	 int left = f[1][2],num1 = 2;
	 int right = f[many - 1][many],num2 = many - 1;
	 while(1){
	 	if(left < right){
	 		left += f[num1][num1 + 1];
	 		num1 ++;
	 	}
	 	if(right < left){
	 		right += f[num2][num1 - 1];
	 		num2 --;
	 	}
	 	if(num1 == num2)break;
	 }
	 for(int i = 1;i <= many;i ++){
	 	if(i == num1)continue;
	 	int now = 0;
	 	if(i < num1){
	 		for(int j = i;j <= num1 - 1;j ++)now += f[j][j + 1];
	 	}
	 	else if(i > num1){
	 		for(int j = num1;j <= i - 1;j ++)now += f[j][j + 1];
	 	}
	 	ans += now * a[i].num;
	 }
	 cout<<a[num1].name<<" "<<ans;
	 return 0;
}
```


