#include <bits/stdc++.h> 
#define int long long 
using namespace std;

int pow_mod(int a,int i,int n)//快速幂
{
	if(!i)
	    return 1%n;
	int tmp=pow_mod(a,i>>1,n);
	tmp=tmp*tmp%n;
	if(i&1)
	    tmp=tmp*a%n;
	return tmp;
}

int test(int n,int a,int d)//Miller-Rabin
{
	if(n==2||n==a)
	    return 1;

	while(!(d&1))
	    d>>=1;

	int t=pow_mod(a,d,n);

	while((d!=n-1)&&(t!=1)&&(t!=n-1)){
		t=t*t%n;
		d<<=1;
	}

	return (t==n-1 || (d&1)==1);
}

int isprime(int n)
{
	for(int i=1;i<=5;++i)
	{
		int r=rand()%(n-2)+1;//随机取数
		if(!test(n,r,n-1))
		    return 0; 
	}
	return 1;
}

