{
    mx ans;
    for(int i=1;i<=2;i++) for(int j=1;j<=2;j++) ans.m[i][j]=0;
    for(int i=1;i<=2;i++)
      for(int j=1;j<=2;j++)
        for(int k=1;k<=2;k++)
          ans.m[i][j]=(ans.m[i][j]+(a.m[i][k]*b.m[k][j])%mod)%mod;
