```cpp
#include <iostream>
#include <algorithm>
#include <map>
using namespace std;

map<int,int> m;

int gcd(int a,int b){
    if(a<b) return gcd(b,a);
    if(b == 0) return a;
    return gcd(b,a%b);
}

int fib(int n){
    if(n<2) return n;
    long long h1,h2;
    if(m.count((n>>1)-1)) h1 = m[(n>>1)-1];
    else h1 = fib((n>>1)-1);
    if(m.count(n>>1)) h2 = m[n>>1];
    else h2 = fib(n>>1);
    if(n&1){//当n为奇数时，F(n) = F(n/2)^2+[F(n/2-1)+F(n/2)]^2
        h1 += h2;
        int f = (h1*h1+h2*h2)%100000000;
        m[n] = f;
        return f;
    }
    else{//当n为偶数时，F(n) = F(n/2)\*[F(n/2)+2\*F(n/2-1)]
        int f = h2*(h2+2*h1)%100000000;
        m[n] = f;
        return f;
    }
}

int main()
{
    int x,y,z;
    cin>>x>>y;
    z = gcd(x,y);//gcd(F(n),F(m)) = F(gcd(n,m))
    cout<<fib(z)<<endl;
}

