```cpp
#include<bits/stdc++.h>
#define mod 100000000
using namespace std;
typedef long long ll;

namespace solve{
    struct m{ll x[2][2];}a,f;
    ll n;

    inline ll gcd(ll a,ll b)
    { // 最大公约数
        if(a==0)
            return b;
        if(b==0)
            return a;
        return gcd(b,a%b);
    }

    m mul(m a,m b)
    {
        m c;
        for(ll i=0,j,k;i<2;i++)
            for(j=0;j<2;j++)
                for(k=0,c.x[i][j]=0;k<2;k++)
                    c.x[i][j]=(c.x[i][j]+a.x[i][k]*b.x[k][j])%mod;
        return c;
    }

    m idm()
    { // '1'矩阵 
        m c;
        c.x[0][0]=c.x[1][1]=1;
        c.x[1][0]=c.x[0][1]=0;
        // 1 0 
        // 0 1 
        return c;
    }

    m ksm(m mt,ll p)
    { // 标准快速幂
        m ret=idm(); // '1'矩阵
        for(;p;p>>=1,mt=mul(mt,mt)) // 标准操作
            if(p&1) // p mod 2 == 0
                ret=mul(ret,mt); // mul
        return ret;
    }

    void init()
    {
        ll k1,k2;
        scanf("%lld%lld",&k1,&k2);
        n=gcd(k1,k2);
        // f[n]=f[gcd(k1,k2)]
        a.x[0][0]=0;
        a.x[0][1]=a.x[1][1]=a.x[1][0]=1;
        // 0 1
        // 1 1
    }

    void getksm()
    {
        f=ksm(a,n-1);
    }

    ll modP(ll s)
    {
        return s%mod;
    }

    int _main()
    {
        init();
        getksm();
        cout<<modP(f.x[0][0]+f.x[0][1])<<endl;
        return 0;
    }
}
using namespace solve; 

int main()
{
    _main();
}
```
