inline void Res()
{
	//tmp.clear();
	memset(tmp,0,sizeof(tmp));
	for(int i=0;i<2;i++)
		for(int j=0;j<2;j++)
			for(int k=0;k<2;k++)
				tmp[i][j]=(tmp[i][j]+res[i][k]*mul[k][j])%100000000;//当时没有%错了几次神仙错误233333

	for(int i=0;i<2;i++)
		for(int j=0;j<2;j++)
			res[i][j]=tmp[i][j];
}

inline void Mul()
{
	//tmp.clear();
	memset(tmp,0,sizeof(tmp));
	for(int i=0;i<2;i++)
		for(int j=0;j<2;j++)	
			for(int k=0;k<2;k++)
				tmp[i][j]=(tmp[i][j]+mul[i][k]*mul[k][j])%100000000;//边运算边%

	for(int i=0;i<2;i++)
