```cpp
#include<iostream>
#include<cstdio>
#define ll long long
using namespace std;
ll p;
ll gcd(ll a,ll b) { //先求GCD
	if(b==0) {
		return a;
	} else {
		return gcd(b,a%b);
	}
}
struct node {//这个结构体存Fibonacci
	ll num[10][10];
	friend node operator * (node x1,node x2);
};
node operator * (node x1,node x2) {//这个重载+ （不知道我为什么写*）
	int i,j,k;
	node x3;
	for(i=1; i<=2; i++) {
		for(j=1; j<=2; j++) {
			x3.num[i][j]=0;
			for(k=1; k<=2; k++) {
				x3.num[i][j]=(x3.num[i][j]%p+(x1.num[i][k]%p*x2.num[k][j]%p)%p)%p;
			}
		}
	}
	return x3;
}
int main() {
	p=100000000;
	ll n,m,k;
	cin>>n>>m;
	k=gcd(n,m);//先求一下nm的GCD
	node x1;
	x1.num[1][1]=1;//初始化
	x1.num[1][2]=1;
	x1.num[2][1]=1;
	x1.num[2][2]=0;
	node ans;
	ans.num[1][1]=1;
	ans.num[1][2]=1;
	ans.num[2][1]=0;
	ans.num[2][2]=0;
	if(k==1||k==2) {//如果GCD(n,m)是1或者2那么公约数就是1
		cout<<1;
