void fen(int n)
{
	m=0;
	for(int i=2;i<=n;i++)
	{
		if(n%i==0)
		{
			p[++m]=i;
			c[m]=0;
			while(n%i==0)
			{
				n/=i;
				c[m]++;
			}
		}
	}
	if(n>1)
	{
		p[++m]=n;
		c[m]=1;
	}//p[]为质因数表
