const int N=1000005;  
const int INF=~0U>>1;
const int mod=10007;
struct node
{
       int fix,l,r,fa,key;
       char c;
       bool operator <(node a)const
       {
            return key<a.key;
       }
}T[N];
int n;
void init()
{
     for(int i=1;i<=n;++i)
     T[i].fa=T[i].l=T[i].r=0;
     T[0].fix=-INF;
}
int t[N];
int build(int x)
{
     int top=0;
     t[++top]=1;
     for(int i=2;i<=x;++i)
     {
             while(top>=0&&T[i].fix<T[t[top]].fix)
             --top;
             if(top)
             {
                    T[i].fa=t[top];
                    T[i].l=T[t[top]].r;
                    T[T[t[top]].r].fa=i;
                    T[t[top]].r=i;
                    t[++top]=i;
             }
             else
             {
                 T[t[1]].fa=i;
                 T[i].l=t[1];
                 t[++top]=i;
             }
     }
     return t[1];
}
