     f[x][0]=(f[T[x].l][0]*f[T[x].r][1]+f[T[x].l][1]*f[T[x].r][0]+f[T[x].l][0]*f[T[x].r][0])%mod;
     }
}
char a[N];
int main()
{
    scanf("%d",&n);
    scanf("%s",a+1);
    int p=0;
