#include<bits/stdc++.h>
using namespace std;
char st[100005];
const int mod=10007;
int w=0,f[100005][2];
int main()
{
	int len;
	string fin="_";
	char tmp;
	cin>>len;
	for(int i=0;i<len;i++)
	{
		cin>>tmp;
		if(tmp=='('||tmp=='*')
			st[w++]=tmp;
		if(tmp=='+')
		{
			while(w!=0&&st[w-1]=='*')
			{
				w--;
				fin+=st[w];
			}
			st[w++]=tmp;
		}
		if(tmp==')')
		{
			while(st[w-1]!='(')
			{
				w--;
				fin+=st[w];
			}
			w--;
		}
		if(tmp!='('&&tmp!=')')
			fin+="_";
	}
	while(w!=0)
	{
		w--;
		fin+=st[w];
	}
	len=fin.size();
	int j=0;
	for(int i=0;i<len;i++)
		if(fin[i]=='_')
		{
			j++;
			f[j][0]=f[j][1]=1;
		}
		else if(fin[i]=='*')
		{
			j--;
			f[j][0]=(f[j][1]*f[j+1][0])%mod+(f[j][0]*f[j+1][1])%mod+(f[j][0]*f[j+1][0])%mod;
			f[j][1]=(f[j][1]*f[j+1][1])%mod;
		}
		else
		{
			j--;
			f[j][1]=(f[j][0]*f[j+1][1])%mod+(f[j][1]*f[j+1][0])%mod+(f[j][1]*f[j+1][1])%mod;
			f[j][0]=(f[j][0]*f[j+1][0])%mod;
		}
