#include<bits/stdc++.h>
#define M 10007
using namespace std;
typedef long long lint;
int l;
string s;
stack<char>tmp;
queue<char>str;
struct node{
    int v0,v1;
}ONE;
stack<node>sta;
void to_suffix(){
    str.push('_');
    for(int i=0;i<l;++i){
        if(s[i]=='*'||s[i]=='('){
            tmp.push(s[i]);
        }
        if(s[i]=='+'){
            while(!tmp.empty()&&tmp.top()=='*'){
                tmp.pop();
                str.push('*');
            }
            tmp.push('+');
        }
        if(s[i]==')'){
            while(tmp.top()!='('){
                str.push(tmp.top());
                tmp.pop();
            }
            tmp.pop();
        }
        if(s[i]!='('&&s[i]!=')'){
            str.push('_');
        }
    }
    while(tmp.size()){
        str.push(tmp.top());
        tmp.pop();
    }
}
int main(){
    ios::sync_with_stdio(false);
    cin>>l>>s;
    to_suffix();
    ONE.v0=ONE.v1=1;
    while(str.size()){
        if(str.front()=='_'){
            sta.push(ONE);
        }
        if(str.front()=='+'){
            node x,y,NEW;
            x=sta.top();
            sta.pop();
            y=sta.top();
            sta.pop();
            NEW.v0=x.v0*y.v0%M;
            NEW.v1=(x.v0*y.v1+x.v1*y.v1+x.v1*y.v0)%M;
            sta.push(NEW);
        }
        if(str.front()=='*'){
            node x,y,NEW;
            x=sta.top();
            sta.pop();
            y=sta.top();
            sta.pop();
            NEW.v1=x.v1*y.v1%M;
            NEW.v0=(x.v0*y.v1+x.v0*y.v0+x.v1*y.v0)%M;
            sta.push(NEW);
        }
        str.pop();
    }
    cout<<sta.top().v0%M<<endl;
    return 0;
}

