bool hefa[7000001];
int d[101*2*2*2];
long long ans1,ans2;
int main()
{
    scanf("%d%d%d",&n,&k,&p);
    for(int i=1;i<=n;i++)
    {
        scanf("%d%d",&a[i].se,&a[i].mo);
        se[a[i].se]++;
        if(a[i].mo>p)
          hefa[i]=false;
        else
          hefa[i]=true;
        if(a[i].mo<=p)
          c[++len]=i;
    }
    for(int i=0;i<=50;i++)
      ans1+=(se[i]*(se[i]-1)/2);
    c[0]=1;
    c[++len]=n+1;
    for(int i=0;i<=len-1;i++)
    {
        int l=c[i],r=c[i+1];
        if(a[c[i]].se>p) continue;
        if(l==r) continue;
        memset(d,0,sizeof(d));
        for(int j=l+1;j<=r-1;j++)
          d[a[j].se]++;
        for(int j=0;j<=50;j++)
          ans2=ans2+(d[j]*(d[j]-1)/2);
    }
    printf("%lld\n",ans1-ans2);
    return 0;
}
