    while(c>='0'&&c<='9')x=(x<<3)+(x<<1)+(c^48),c=getchar();
    return x*k;
}
#define inf 123456789
int main()
{
    n=read(),k=read(),p=read();
    for(int i=1;i<=n;i++)
    {
        a[i].c=read();
        a[i].m=read();
    }
