}
vector<int> q[50005];//用邻接表存储每一个颜色的数量
int main()
{
    n=read(),k=read(),p=read();
    for(int i=1;i<=n;i++)
    {
        c=read(),m[i]=read();
        q[c].push_back(i);
    }
    build(1,1,n);
    //枚举每一个颜色
    for(int i=0;i<k;i++)
    {
    	//找到该色所有的节点
        for(int l=0;l<q[i].size();l++)
        {
            for(int j=l+1;j<q[i].size();j++)
            {
                int a=q[i][l],b=q[i][j];
                if(a>b)
                {
                    swap(a,b);
