int n;
int A[200001];
int logn[200001];
int ST[200010][200];

void insert_ST()
{
	logn[0]=-1;//log[0]在数学中不存在,这里是为了方便递推 
	for (int i=1;i<=n;i++)
	{
		logn[i]=logn[i>>1]+1;//递推计算log[i]的值 
		ST[i][0]=A[i];//顺便把数组A[i]存入ST表,因为2^0-1=0,所以ST[i][0]就是A(i)~A(i+0)最值也就是A[i] 
