		{
			ST[j][i]=minn(ST[j][i-1],ST[j+(1<<(i-1))][i-1]);//把ST表j~j+2^i-1拆成j~j+2^(i-1)-1和j+2^(i-1)~j+2^i-1两部分，动态规划合并区间 
		}
	}
}
