int main()
{
	scanf("%d%d%d",&n,&k,&t);
	for(int i=1;i<=n;i++)
	{
		scanf("%d%d",&col[i],&p[i]);
		pre[col[i]]++;//预处理
	}

	for(int i=1;i<=n;i++)
	{
		sum[col[i]]++;
		if(p[i]<=t)//满足条件
		for(int j=0;j<k;j++)//扫一遍颜色
		{
			if(sum[j])
			{
				if(col[i]==j)//这里很恶心,如果cafe在
                             //区间的边界上,我们要把
                             //整个左区间(减去这个
                             //cafe的客栈数)和这个cafe
                             //配对,不然就把它跳过了

                {
					ans+=sum[j]-1;
				}
				//cout<<i<<" "<<j<<" "<<sum[j]<<" "<<pre[j]<<endl;
				ans+=sum[j]*(pre[j]-sum[j]);
				pre[j]-=sum[j];sum[j]=0;
/*减去已经用过的点,因为每次计算都所用的左区间不固定，
而右区间一定是从i到n，所以如如果不减掉的话，相当于每找到
一个cafe都要算一遍*/


			}
    	}
	}
	cout<<ans;
}
