```cpp
#include <iostream>
#include <algorithm>
#include <cstring>
#include <cmath>
#include <cstdio>
using namespace std;
const int N = 200003;
const int Lg = log2(N) + 1;
const int K = 50;
struct Edge {
    int v, next;
};
Edge edge[N];
int first[K], point[N];
int cost[N], rmin[25][N], lg2[N];
int n, k, p, num_edge;
inline void add_edge(int &u, int &v);
void st_init();
int rminq(const int &l, const int &r);
void read(int &res);
int main()
{
    read(n); read(k); read(p);
    int color;
    for (int i = 1; i <= n; ++i) {
        read(color); read(cost[i]);
        add_edge(color, i);
    }
    st_init();
    int cnt = 0, &num = point[0];
    for (int col = 0; col < k; ++col) {
        num = 0;
        for (int i = first[col]; i != 0; i = edge[i].next)
            point[++num] = edge[i].v;
        // 为何倒序？因为临界表存的是反的 
        reverse(point + 1, point + 1 + num);
        for (int i = 1; i < num; ++i)
            for (int j = i + 1; j <= num; ++j) {
                if (cost[rminq(point[i], point[j])] <= p) {
                    cnt += num - j + 1;
                    break;
                }
            }
    }
    cout << cnt << "\n";
    return 0;
}
inline void add_edge(int &u, int &v)
{
    edge[++num_edge] = {v, first[u]};
    first[u] = num_edge;
}
void st_init()
{
    for (int i = 1, k = 0; i <= n; ++i) {
        lg2[i] = k - 1;
        if ((1 << k) == i) ++k;
        rmin[0][i] = i;
    }
    for (int j = 1; j <= Lg; ++j)
        for (int i = 1; i + (1 << j) - 1 <= n; ++i)
            rmin[j][i] = rminq(rmin[j - 1][i], rmin[j - 1][i + (1 << (j - 1))]);
}
int rminq(const int &l, const int &r)
{
    if (l == r) return l;
    int k = lg2[r - l + 1];
    int &x = rmin[k][l], &y = rmin[k][r - (1 << k) + 1];
    return (cost[x] < cost[y]) ? x : y;
}
void read(int &res)
{
    res = 0; char ch = getchar();
    while (!isdigit(ch)) ch = getchar();
    while (isdigit(ch)) {
        res = res * 10 + ch - '0';
        ch = getchar();
    }
}
