int num=0;
short coler[COLER]={0};

void pre()
{
    int i;

    scanf("%d %d %d",&n,&k,&p);
    street=(hotel*)malloc(sizeof(hotel)*n);

    for(i=0;i<n;++i)
    {
        scanf("%d %d",&street[i].k,&street[i].p);
        ++coler[street[i].k];
