    int i,j;

    pre();

    for(i=0;i<n;++i)
    {
        int c=0; 
        --coler[street[i].k];

        j=i;

        while(street[j].p>p)
        {
            if(street[j].k==street[i].k&&j!=i)
            {
                ++c;
            }
            ++j;
        }

        num=num+coler[street[i].k]-c;
    }

    printf("%d",num);

