    for j:=1 to a[i,0]-1 do  //为什么是到a[i,0]-1？还是留给大家到题目中找答案吧
    begin
      bo:=true;
      l:=a[i,j];
      if b[l]<=p then bo:=false;
      while (bo) and (l<a[i,a[i,0]]) do
      begin
        inc(l);
        if b[l]<=p then bo:=false;
      end;
      if bo=false then
      begin
        m:=j;
        while a[i,m]<l do
          inc(m);
