 void Qsort(int l,int r)
{
	int i,j,x,tmp;
	x=w1[(l+r)/2]; i=l; j=r;
	while (i<=j)
	{
		while (w1[i]<x) i++;
		while (w1[j]>x) j--;
		if (i<=j)
		{
			tmp=w1[i]; w1[i]=w1[j]; w1[j]=tmp;
			i++; j--;
		}
	}
	if (i<r) Qsort(i,r);
	if (l<j) Qsort(l,j);
}

 long long ss(int x)
{
	sumw[0]=0; sumv[0]=0;
	for (int i=1; i<=n; i++)
		if (w[i]<=x)
