```cpp
#include<bits/stdc++.h>
using namespace std;
#define int long long
int g[200020],gl[200020],w[200020],v[200020],l[200020],r[200020];
int n,m,s,lmin=0x3f3f3f3f3f3f,rmax,ans=0x3f3f3f3f3f3f;
#define rg register
inline int read(){
	rg char ch=getchar();
	rg int x=0,f=0;
	while(!isdigit(ch)) f|=(ch=='-'),ch=getchar();
	while(isdigit(ch)) x=(x<<1)+(x<<3)+(ch^48),ch=getchar();
	return f?-x:x;
}
int lb(int x){
	return x&-x;
}
void add(int x,int *g,int d){
	for(int i=x;i<=n;i+=lb(i)){
		g[i]+=d;
	}
}
int ask(int x,int *g){
	int sum=0;
	for(int i=x;i;i-=(lb(i))){
		sum+=g[i];
	}
	return sum;
}
bool check(int x){
	memset(g,0,sizeof g);
	memset(gl,0,sizeof gl);
	for(int i=1;i<=n;++i){
		if(w[i]>=x){
			add(i,g,1);
			add(i,gl,v[i]);
		}
	}
	int sum=0;
	for(int rl,rr,i=1;i<=m;++i){
		rr=ask(r[i],gl)-ask(l[i]-1,gl);
		rl=ask(r[i],g)-ask(l[i]-1,g);
		sum+=rr*rl;
	}
	ans=min(ans,llabs(sum-s));
	if(sum>s) return true;
	return false;
}
