	for(int i=1;i<=n;++i) w[i]=read(),v[i]=read(),lmin=min(lmin,w[i]),rmax=max(w[i],rmax);
	for(int i=1;i<=m;++i) l[i]=read(),r[i]=read();
	int l=lmin-1,r=rmax+1,mid;
	while(l<=r){
		mid=l+r>>1;
		if(check(mid)) l=mid+1;
		else r=mid-1;
	}
	cout<<ans<<endl;
	return 0;
}

