        }
        else
            continue;//为什么要continue呢？ 如果这里不直接continue 那么遇到一个坑接着平地就会算作是一个新的坑
        if (rem[0] > rem[1] && rem[1] < rem[2])//很好理解 如果这是个坑
            Count++;//计数+1
    }
    cout << Count;
    return 0;
}

