    int n,s=0;
    cin>>n;
    for(int i = 1;i <= n; i++)
    {
        cin>>a[i];
    }
    for(int i=1;i<=n;i++)
    {
        b[i]=max(b[i-1],a[i]);//最大值=max{上一个的最大值,这个格子的高度}
    }
    for(int i=n;i>=1;i--)
    {
        c[i]=max(c[i+1],a[i]);//最大值=max{后一个的最大值,这个格子的高度}
    }
    for(int i=1;i<=n;i++)
    {
        if(a[i]<min(c[i],b[i])) s+=min(c[i],b[i])-a[i];//计算结果直接用
    }
    cout<<s;
    return 0;
}

