    int maxx=num[1],mid=1;
    for(re int i=2;i<=n;i++)
        if(num[i]>maxx)
        {
            maxx=num[i];
            mid=i;//找到最高峰 
        }
    int high=num[1];
    for(re int i=2;i<mid;i++)
    {
        high=max(high,num[i]);
        ans+=high-num[i];
    }
    high=num[n];
    for(re int i=n-1;i>mid;i--)
